	public static Button createIconButton(String svg, int width, int height, int scale) {
		SVGPath path =createSVGGraphic(svg, scale);
		Button button = new Button();
		button.setContentDisplay(ContentDisplay.CENTER);
		button.setPickOnBounds(true);
		button.setGraphic(path);
		button.setAlignment(Pos.CENTER);
		button.setMinSize(width, height);
		button.setMaxSize(width, height);
		return button;
	}
	
	public static SVGPath createSVGGraphic(String svg, int scale) {
		SVGPath path = new SVGPath();
		path.setContent(svg);
		path.setStyle("-fx-fill: -text-fill-color;");
		Bounds bounds = path.getBoundsInLocal();
		double scaleFactor = scale / Math.max(bounds.getWidth(), bounds.getHeight());
		path.setScaleX(scaleFactor);
		path.setScaleY(scaleFactor);
		return path;
	}

